/*	Attribution and link to data sources
	============================================= */

table.sources			{width:960px; margin:0 auto; color:#fff; table-layout:fixed;}
table.sources th		{text-align:left; font-weight:bold; padding:20px 20px 20px 0;}
table.sources th.col1	{width:25%;}
table.sources th.col2	{width:40%;}
table.sources th.col3	{width:35%;}
table.sources td		{padding:10px 20px 10px 0; line-height:1.45em; border-bottom:1px solid #505050; vertical-align:bottom;}
table.sources td:hover	{cursor:default;}
table.sources td:first-child	{color:#D0DEF0;}
table.sources td a		{color:#5E96DD;}
table.sources td a:hover{color:#A5CDFF; text-decoration:underline;}


/*	Github
	============================================= */

.gitcon		{display:inline-block; vertical-align:top; width:640px; max-width:640px;}
.gitinfo	{width:640px; padding-top:10px; font-size:18px; line-height:1.3em;}
.gitinfo p	{color:#444; line-height:25px; font-size:18px;}
.octocat	{display:inline-block; vertical-align:top; width:300px; max-width:300px; margin-left:20px;}
.gitinfo a	{color:#1E90FF;}
.gitinfo a:hover	{text-decoration:underline;}
a.git		{display:block; font-size:38px; background-color:#1E90FF; border-radius:14px; text-align:center; padding:20px 0; color:#fff; width:640px; box-sizing:border-box;}
a.git:hover	{background-color:#66b4ff; text-decoration:none;}
